import React from 'react';
import Head from 'next/head';
import { useQuery , useMutation} from '@apollo/react-hooks';
import gql from 'graphql-tag';

const TASKS_QUERY = gql`
    {
      tasks {
        uuid
        description
        entry
        modified
        status
        urgency
      }
    }
`;

const COMPLETE_TODO = gql`
    mutation CompleteTask($uuid: ID!) {
      completeTask(uuid: $uuid) {
        uuid
        description
        entry
        modified
        status
        urgency
      }
    }
`;

const Home = () => {
  const { data, loading, error } = useQuery(TASKS_QUERY);
    const [ completeTodo ] = useMutation(COMPLETE_TODO, {
      update(cache, { data: { addTodo } }) {
        const { todos } = cache.readQuery({ query: TASKS_QUERY });
        cache.writeQuery({
          query: TASKS_QUERY,
          data: { todos: todos.concat([addTodo]) },
        });
      }
});

  const updateCache = (cache, {data}) => {
    // Fetch the todos from the cache
    const existingTodos = cache.readQuery({
      query: TASKS_QUERY
    });
    // Add the new todo to the cache
    const newTodo = data.insert_todos.returning[0];
    cache.writeQuery({
      query: GET_MY_TODOS,
      data: {todos: [newTodo, ...existingTodos.todos]}
    });
  };


  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {JSON.stringify(error)}</p>;
  }

  return (
    <div>
      <Head>
        <title>Home</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <ul>
        {data.tasks.map(task => {
            let input

            return <li key={task.uuid}>
                <input 
                 ref={node => {
                  input = node;
                }}
                type="checkbox" 
                checked={task.status === "completed"}
                onChange={e => {
                    console.log(task.status)
                    e.preventDefault();
                    task.status !== "completed"
                        ? completeTodo({ variables: { uuid: task.uuid }})
                        : null
                }}
            />{task.description}</li>;
        })}
      </ul>
    </div>
  );
};

export default Home;
